mapper.update:
update login_account_info
set last_login_time = #{lastLoginTime,jdbcType=VARCHAR}
where account = #{account,jdbcType=VARCHAR}

mapper.select:
select id, account, name, mobile, dept_code, dept_name, email, ID_card, first_login_time,last_login_time
from login_account_info
where account = #{account,jdbcType=VARCHAR}
