mapper.update:
update sunmnet_user_info


        user_status = #{userStatus,javaType=UserStatusEnum},


        last_modify_time = #{lastModifyTime,jdbcType=VARCHAR}


where user_no = #{userNo,jdbcType=VARCHAR}

mapper.select:

	 select user.user_no,user.user_name,user.user_nickname,user.user_mobile,user.department_no,department.department_name,	        
	        	user.user_mail,user.user_status,user.relation_type,user.relation_no,user.create_time,user.last_modify_time
        from sunmnet_user_info user left join sunmnet_department_info department on user.department_no = department.department_no
        where 1=1 

            and user.department_no = #{departmentNo,jdbcType=VARCHAR}             


            and user.user_name  LIKE CONCAT('%',#{userName,jdbcType=VARCHAR},'%' )


            and user.user_mobile  = #{userMobile,jdbcType=VARCHAR}


            and user.user_status = #{userStatus,javaType=UserStatusEnum}


             and user.relation_type = #{relationType,javaType=RelationTypeEnum}


            and user.relation_no = #{relationNo,jdbcType=VARCHAR}


            and user.user_no = #{userNo,jdbcType=VARCHAR}


			and user.create_time  >=  #{beginTime,jdbcType=VARCHAR}
			and user.create_time   <=   #{endTime,jdbcType=VARCHAR}


	 select user.user_no,user.user_name,user.user_nickname,user.user_mobile,user.department_no,department.department_name,	        
	        	user.user_mail,user.user_status,user.relation_type,user.relation_no,user.create_time,user.last_modify_time
        from sunmnet_user_info user left join sunmnet_department_info department on user.department_no = department.department_no
        where 1=1 

            and user.department_no = #{departmentNo,jdbcType=VARCHAR}             


            and user.user_name  LIKE CONCAT('%',#{userName,jdbcType=VARCHAR},'%' )


            and user.user_mobile  = #{userMobile,jdbcType=VARCHAR}


            and user.user_status = #{userStatus,javaType=UserStatusEnum}


             and user.relation_type = #{relationType,javaType=RelationTypeEnum}


            and user.relation_no = #{relationNo,jdbcType=VARCHAR}


            and user.user_no = #{userNo,jdbcType=VARCHAR}


			and user.create_time  >=  #{beginTime,jdbcType=VARCHAR}
			and user.create_time   <=   #{endTime,jdbcType=VARCHAR}


			order by ${sort} ${order}
